import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getUserFromToken } from '../utils/auth';  // Utility function to decode token

const Header = () => {
  const [username, setUsername] = useState('');

  useEffect(() => {
    // Retrieve username from token after login
    const user = getUserFromToken();  // Decode the token and get user details
    if (user) {
      setUsername(user.username);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');  // Remove token from localStorage
    window.location.href = '/login';   // Redirect to login page
  };

  return (
    <header>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/courses">Courses</Link></li>
          <li>
            {username && (
              <span>Welcome, {username}</span>  // Display username here
            )}
          </li>
          <li><button onClick={handleLogout}>Logout</button></li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
