import React from 'react';
import { Link, useHistory } from 'react-router-dom';
import { getUserFromToken } from '../utils/auth';  // Utility function to decode token

const Header = () => {
  const [username, setUsername] = useState('');
  const history = useHistory();

  useEffect(() => {
      // Retrieve username from token after login
      const user = getUserFromToken();  // Decode the token and get user details
      if (user) {
        setUsername(user.username);
      }
    }, []);

  const handleLogout = () => {
    // Clear the token from local storage and redirect to login
    localStorage.clear();
    history.push('/login');
  };

  return (
    <header style={styles.header}>
      <h1 style={styles.logo}>Student Management System</h1>
      <nav>
        <ul style={styles.navList}>
          <li style={styles.navItem}>
            <Link to="/" style={styles.navLink}>Home</Link>
          </li>
          <li style={styles.navItem}>
            <Link to="/student-dashboard" style={styles.navLink}>Dashboard</Link>
          </li>
              <li>
            {username && (
              <span>Welcome, {username}</span>  // Display username here
            )}
          </li>
          <li style={styles.navItem}>
            <button onClick={handleLogout} style={styles.logoutButton}>Logout</button>
          </li>
        </ul>
      </nav>
    </header>
  );
};

// Styling for the Header component
const styles = {
  header: {
    backgroundColor: '#007bff',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  logo: {
    color: 'white',
    margin: 0,
  },
  navList: {
    listStyleType: 'none',
    display: 'flex',
    margin: 0,
    padding: 0,
  },
  navItem: {
    marginLeft: '20px',
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    fontSize: '18px',
  },
  logoutButton: {
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '8px 16px',
    fontSize: '16px',
    cursor: 'pointer',
    borderRadius: '5px',
  }
};

export default Header;
